# use Turtle Graphics to draw the picture. set up the canvas
import turtle
turtle.setup()

for i in range(0, len(secret), 3):
    # when encounter the actions character "S", pick up the pen and go to the x-y coordinates
    # described by the two values on the two positions directly after "S"
    if secret[i] == "S":
        turtle.penup()
        turtle.goto(secret[i+1], secret[i+2])
        turtle.pendown()
    # when encounter the actions character "P", draw a line to the point of the x-y coordinates
    # described by the two values on the two positions directly after "P"
    # the pencolor should be black when drawing a line
    elif secret[i] == "P":
        turtle.pencolor("black")
        turtle.goto(secret[i+1], secret[i+2])
    # when encounter the actions character "E", stop drawing and pick up the pen
    elif secret[i] == "E":
        turtle.penup()
